package com.purushoth.day2;

public class Sum_Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int  arr[]=new int[] {5,15,25,35};
		int sum=0;
	    for(int i=0;i<arr.length;i++) {
	    	sum +=arr[i];
	    }
	    System.out.println("Sum is: "+sum);
		

	}

}//Sum of given array
